create table city (id bigint not null auto_increment, name varchar(255) not null, state_id bigint not null, primary key (id)) engine=InnoDB;
create table cuisine (id bigint not null auto_increment, name varchar(255) not null, primary key (id)) engine=InnoDB;
create table payment_method (id bigint not null auto_increment, description varchar(255) not null, primary key (id)) engine=InnoDB;
create table permission (id bigint not null auto_increment, description varchar(255), name varchar(255) not null, primary key (id)) engine=InnoDB;
create table product (id bigint not null auto_increment, active bit not null, description varchar(255) not null, name varchar(255) not null, price decimal(19,2) not null, restaurant_id bigint not null, primary key (id)) engine=InnoDB;
create table restaurant (id bigint not null auto_increment, address_complement varchar(255), address_district varchar(255), address_number varchar(255), address_postal_code varchar(255), address_street varchar(255), created_at datetime not null, freight_rate decimal(19,2) not null, name varchar(255) not null, updated_at datetime not null, address_city_id bigint, cuisine_id bigint not null, primary key (id)) engine=InnoDB;
create table retaurant_payment_method (restaurant_id bigint not null, payment_method_id bigint not null) engine=InnoDB;
create table state (id bigint not null auto_increment, name varchar(255) not null, primary key (id)) engine=InnoDB;
create table user (id bigint not null auto_increment, created_at datetime not null, email varchar(255) not null, name varchar(255) not null, password varchar(255) not null, primary key (id)) engine=InnoDB;
create table user_group_permission (user_group_id bigint not null, permission_id bigint not null) engine=InnoDB;
create table user_user_group (user_id bigint not null, user_group_id bigint not null) engine=InnoDB;
create table user_group (id bigint not null auto_increment, name varchar(255) not null, primary key (id)) engine=InnoDB;
